import React from 'react';
import moment from 'moment';
import './style.css';

export default class Calendar extends React.Component {
  state = {
    dateContext: moment(),
    today: moment(),
    showMonthPopup: false,
    showYearPopup: false
  }

  constructor(props) {
    super(props);
    this.width = props.width || "350px";
    this.style = props.style || {};
    this.style.width = this.width;
  }

  weekdays = moment.weekdays();
  weekdaysMin = moment.weekdaysMin();
  months = moment.months();

  nextMonth = () => {
    let dateContext = Object.assign({}, this.state.dateContext);
    dateContext = moment(dateContext).add(1, "month");
    this.setState({
      dateContext: dateContext
    });
    this.props.onNextMonth && this.props.onNextMonth();
  }

  prevMonth = () => {
    let dateContext = Object.assign({}, this.state.dateContext);
    dateContext = moment(dateContext).subtract(1, "month");
    this.setState({
      dateContext: dateContext
    });
    this.props.onPrevMonth && this.props.onPrevMonth();
  }

  year = () => {
    return this.state.dateContext.format("Y");
  }
  month = () => {
    return this.state.dateContext.format("MMMM");
  }

  monthShort = () => {
    return moment().format("MMM, D");
  }

  daysInMonth = () => {
    return this.state.dateContext.daysInMonth();
  }
  currentDate= () => {
    return this.state.dateContext.get("date");
  }
  currentDay = () => {
    return this.state.dateContext.format("D");
  }

  firstDayOfMonth = () => {
    let dateContext = this.state.dateContext;
    let firstDay = moment(dateContext).startOf('month').format('d');
    return firstDay;
  }


  SelectList = (props) => {
    let popup = props.data.map((data) => {
      return (
        <div key = {data}>
          <a href="#" onClick={(e)=> {this.onSelectChange(e, data)}}>
            {data}
          </a>
        </div>
      );
    });

    return (
      <div className="month-popup">
        {popup}
      </div>
    );
  }
  setMonth = (month) => {
    let monthNo = this.months.indexOf(month);
    let dateContext = Object.assign({}, this.state.dateContext);
    dateContext = moment(dateContext).set("month", monthNo);
    this.setState({
      dateContext: dateContext
    });
  }
  onSelectChange = (e, data) => {
    this.setMonth(data);
    this.props.onMonthChange && this.props.onMonthChange();
  }

onChangeMonth = (e, month) => {
  this.setState({
    showMonthPopup: !this.state.showMonthPopup
  })
}

  MonthNav = () => {
    return (
      <span className="label-month"
        onClick={(e)=>{this.onChangeMonth(e, this.month())}}>
        {this.month()}
        {this.state.showMonthPopup &&
        <this.SelectList data={this.months} />
        }
      </span>
    );
  }

  showYearEditor = () => {
    this.setState({
      showYearNav: true
    });
  }

  setYear = (year) => {
    let dateContext = Object.assign({}, this.state.dateContext);
    dateContext = moment(dateContext).set("year", year);
    this.setState({
      dateContext: dateContext
    })
  }
  onYearChange = (e) => {
    this.setYear(e.target.value);
    this.props.onYearChange && this.props.onYearChange(e, e.target.value);
  }

  onKeyUpYear = (e) => {
    if (e.which === 13 || e.which === 27) {
      this.setYear(e.target.value);
      this.setState({
        showYearNav: false
      })
    }
  }
  openSelectionMenu = (e) => {
    document.getElementById("hidden-menu").style.display = "table-row"
    this.props.openSelectionMenu && this.props.openSelectionMenu(e);
  }
  closeSelectionMenu = (e) => {
    document.getElementById("hidden-menu").style.display = "none"
    this.props.closeSelectionMenu && this.props.closeSelectionMenu(e);
  }

  YearNav = () => {
    return (
      this.state.showYearNav ?
      <input
        defaultValue = {this.year()}
        className="editor-year"
        ref={(yearInput) => {this.yearInput = yearInput}}
        onKeyUp= {(e) => this.onKeyUpYear(e)}
        onChange = {(e) => this.onYearChange(e)}
        type="number"
        placeholder="year"/>
        :
      <span className="label-year"
        onDoubleClick={(e) => {this.showYearEditor()}}>
        {this.year()}
      </span>
    );
  }

  render(){

    let weekdays = this.weekdaysMin.map((day) => {
      return (
        <td key = {day} className="week-day">{day}</td>
      )
    });

    let blanks = [];
    for (let i = 0; i < this.firstDayOfMonth(); i++) {
      blanks.push(<td key={i * 80} className="emptySlot">
        {""}
        </td>);
    }

    console.log("blanks: ", blanks);

    let daysInMonth = [];

    for (let d = 1; d <= this.daysInMonth(); d++) {
      let className = (d == this.currentDay() ? "day current-day": "day" );
      if((this.currentDay() === 0)&&(this.currentDay()=== 6)){
      daysInMonth.push(
        <td key={d} className={className}>
          <span>{d}</span>
        </td>

      );
    }else{
      daysInMonth.push(
        <td key={d} className="weekDay" >
          <span>{d}</span>
        </td>
      );
    }
    }

    console.log("days: ", daysInMonth);

    var totalSlots = [...blanks, ...daysInMonth]
    let rows = [];
    let cells = [];

    totalSlots.forEach((row, i) => {
      if((i % 7)!== 0) {
        cells.push(row);
      } else {
        let insertRow = cells.slice();
        rows.push(insertRow);
        cells = [];
        cells.push(row);
      }
      if (i=== totalSlots.length - 1) {
        let insertRow = cells.slice();
        rows.push(insertRow);
      }
    });
    // alert(totalSlots.length);
    let trElems = rows.map((d, i) => {
      return (
        <tr key={i*100}>
          {d}
        </tr>
      );
    });

    return (
      <div className="calendar-container" style={this.style}>
        <table className="calendar">
          <thead className="thead">
            <tr className="calendar-top ">
              <td colSpan="2" className="prev"
                  onClick={(e)=> {this.prevMonth()}}>
                  Prev
                </td>
                <td colSpan="5">
                  <this.MonthNav />
                  {" "}
                  <this.YearNav />
                  {" "}
                  <i className="open fas fa-chevron-down"
                    onClick = {(e) => {this.openSelectionMenu()}}></i>

                  <i className="open fas fa-chevron-up"
                    onClick = {(e) => {this.closeSelectionMenu()}}></i>
                </td>
                <td colSpan="2" className="next"
                  onClick={(e)=> {this.nextMonth()}}>
                  Next
              </td>
            </tr>
            <tr className="weekDay">
              {weekdays}
            </tr>
          </thead>
          <tbody className="calendar-bottom">
          <tr id = "hidden-menu">
            <td>
              week
            </td>
            <td>
              month
            </td>
          </tr>

            {trElems}
          </tbody>
          <tr className="monthShort"><this.monthShort/></tr>
        </table>
      </div>
    );
  }
}
